import{_ as t,c as d,o as a,aj as s}from"./chunks/framework.8cB7ImeA.js";const b=JSON.parse('{"title":"iCloudBridge CLI Usage Guide","description":"","frontmatter":{},"headers":[],"relativePath":"USAGE.md","filePath":"USAGE.md"}'),o={name:"USAGE.md"};function r(i,e,n,c,l,h){return a(),d("div",null,[...e[0]||(e[0]=[s(`<h1 id="icloudbridge-cli-usage-guide" tabindex="-1">iCloudBridge CLI Usage Guide <a class="header-anchor" href="#icloudbridge-cli-usage-guide" aria-label="Permalink to ‚ÄúiCloudBridge CLI Usage Guide‚Äù">‚Äã</a></h1><p>Authoritative reference for every <code>icloudbridge</code> command. Use this when you need to quickly recall which switches exist, what they do, and how the commands interact.</p><ul><li><strong>CLI entry point:</strong> <code>icloudbridge [OPTIONS] COMMAND [ARGS]...</code></li><li><strong>Supported OS:</strong> macOS 13+ (Apple Notes/Reminders APIs require EventKit + AppleScript)</li><li><strong>Python runtime:</strong> 3.11+ (Poetry handles dependencies for you)</li><li><strong>Config:</strong> defaults to <code>~/.icloudbridge/config.toml</code> unless overridden with <code>--config</code></li></ul><blockquote><p>Tip: <code>icloudbridge --help</code>, <code>icloudbridge notes --help</code>, etc. show contextual help at the terminal. This document mirrors that information and adds extra context.</p></blockquote><hr><h2 id="global-options" tabindex="-1">Global Options <a class="header-anchor" href="#global-options" aria-label="Permalink to ‚ÄúGlobal Options‚Äù">‚Äã</a></h2><p>These flags are accepted by every command:</p><table tabindex="0"><thead><tr><th>Option</th><th>Short</th><th>Description</th><th>Default</th></tr></thead><tbody><tr><td><code>--config PATH</code></td><td><code>-c</code></td><td>Use a custom configuration file (must exist). The path you pass is also persisted so future runs can reuse it.</td><td>last-used config or <code>~/.icloudbridge/config.toml</code></td></tr><tr><td><code>--log-level LEVEL</code></td><td><code>-l</code></td><td>Override logging verbosity (<code>DEBUG</code>, <code>INFO</code>, <code>WARNING</code>, <code>ERROR</code>, <code>CRITICAL</code>). When omitted we honor the <code>general.log_level</code> value from the config file.</td><td><code>INFO</code></td></tr><tr><td><code>--help</code></td><td>‚Äî</td><td>Show contextual help for the current command.</td><td>‚Äî</td></tr></tbody></table><p>All examples in this guide assume you are running from the project root or that <code>icloudbridge</code> is installed globally (via <code>poetry run icloudbridge</code>, <code>pipx</code>, etc.).</p><hr><h2 id="top-level-commands" tabindex="-1">Top-Level Commands <a class="header-anchor" href="#top-level-commands" aria-label="Permalink to ‚ÄúTop-Level Commands‚Äù">‚Äã</a></h2><h3 id="version" tabindex="-1"><code>version</code> <a class="header-anchor" href="#version" aria-label="Permalink to ‚Äúversion‚Äù">‚Äã</a></h3><p>Shows the CLI, Python, and macOS build information.</p><div class="language-"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki shiki-themes github-light github-dark" style="--shiki-light:#24292e;--shiki-dark:#e1e4e8;--shiki-light-bg:#fff;--shiki-dark-bg:#24292e;" tabindex="0" dir="ltr"><code><span class="line"><span>icloudbridge version</span></span></code></pre></div><h3 id="config" tabindex="-1"><code>config</code> <a class="header-anchor" href="#config" aria-label="Permalink to ‚Äúconfig‚Äù">‚Äã</a></h3><p>Inspect or scaffold configuration.</p><table tabindex="0"><thead><tr><th>Option</th><th>Description</th></tr></thead><tbody><tr><td><code>--show</code>, <code>-s</code></td><td>Render the current effective configuration in a table.</td></tr><tr><td><code>--init</code>, <code>-i</code></td><td>Create (or overwrite) the default config file with sample values.</td></tr></tbody></table><p>Examples:</p><div class="language-"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki shiki-themes github-light github-dark" style="--shiki-light:#24292e;--shiki-dark:#e1e4e8;--shiki-light-bg:#fff;--shiki-dark-bg:#24292e;" tabindex="0" dir="ltr"><code><span class="line"><span># Create ~/.icloudbridge/config.toml with sample sections</span></span>
<span class="line"><span>icloudbridge config --init</span></span>
<span class="line"><span></span></span>
<span class="line"><span># Inspect settings currently in effect</span></span>
<span class="line"><span>icloudbridge config --show</span></span></code></pre></div><h3 id="photos" tabindex="-1"><code>photos</code> <a class="header-anchor" href="#photos" aria-label="Permalink to ‚Äúphotos‚Äù">‚Äã</a></h3><p>Import media from configured watch folders into Apple Photos.</p><table tabindex="0"><thead><tr><th>Option</th><th>Description</th></tr></thead><tbody><tr><td><code>--dry-run</code></td><td>Scan and print statistics without importing anything.</td></tr><tr><td><code>--source KEY</code>, <code>-s KEY</code></td><td>Limit the run to specific <code>photos.sources</code> keys. Repeat to select multiple sources.</td></tr></tbody></table><div class="language-"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki shiki-themes github-light github-dark" style="--shiki-light:#24292e;--shiki-dark:#e1e4e8;--shiki-light-bg:#fff;--shiki-dark-bg:#24292e;" tabindex="0" dir="ltr"><code><span class="line"><span>icloudbridge photos --dry-run</span></span>
<span class="line"><span>icloudbridge photos --source nextcloud --source screenshots</span></span></code></pre></div><h3 id="db-paths" tabindex="-1"><code>db-paths</code> <a class="header-anchor" href="#db-paths" aria-label="Permalink to ‚Äúdb-paths‚Äù">‚Äã</a></h3><p>Print the file paths for the Notes, Reminders, and Passwords SQLite databases so you can back them up or inspect them with a SQL browser.</p><div class="language-"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki shiki-themes github-light github-dark" style="--shiki-light:#24292e;--shiki-dark:#e1e4e8;--shiki-light-bg:#fff;--shiki-dark-bg:#24292e;" tabindex="0" dir="ltr"><code><span class="line"><span>icloudbridge db-paths</span></span></code></pre></div><h3 id="health" tabindex="-1"><code>health</code> <a class="header-anchor" href="#health" aria-label="Permalink to ‚Äúhealth‚Äù">‚Äã</a></h3><p>Performs a quick readiness check (data directory, database presence, configured folders, CalDAV URL, etc.).</p><div class="language-"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki shiki-themes github-light github-dark" style="--shiki-light:#24292e;--shiki-dark:#e1e4e8;--shiki-light-bg:#fff;--shiki-dark-bg:#24292e;" tabindex="0" dir="ltr"><code><span class="line"><span>icloudbridge health</span></span></code></pre></div><h3 id="serve" tabindex="-1"><code>serve</code> <a class="header-anchor" href="#serve" aria-label="Permalink to ‚Äúserve‚Äù">‚Äã</a></h3><p>Launch the bundled FastAPI backend + WebUI without using the menubar app. Helpful during development or when running on a server/VM.</p><table tabindex="0"><thead><tr><th>Option</th><th>Short</th><th>Description</th><th>Default</th></tr></thead><tbody><tr><td><code>--host</code></td><td><code>-h</code></td><td>Interface to bind. Use <code>0.0.0.0</code> to listen on all interfaces.</td><td><code>127.0.0.1</code></td></tr><tr><td><code>--port</code></td><td><code>-p</code></td><td>TCP port for HTTP/WebSocket traffic.</td><td><code>8000</code></td></tr><tr><td><code>--reload</code></td><td>‚Äî</td><td>Enable code auto-reload (development only).</td><td></td></tr><tr><td><code>--background</code></td><td><code>-d</code></td><td>Detach and keep the server running in the background (launches a new process).</td><td></td></tr></tbody></table><p>Examples:</p><div class="language-"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki shiki-themes github-light github-dark" style="--shiki-light:#24292e;--shiki-dark:#e1e4e8;--shiki-light-bg:#fff;--shiki-dark-bg:#24292e;" tabindex="0" dir="ltr"><code><span class="line"><span>icloudbridge serve --reload</span></span>
<span class="line"><span>icloudbridge serve --host 0.0.0.0 --port 9000</span></span>
<span class="line"><span>icloudbridge serve --background</span></span></code></pre></div><hr><h2 id="notes-commands" tabindex="-1">Notes Commands <a class="header-anchor" href="#notes-commands" aria-label="Permalink to ‚ÄúNotes Commands‚Äù">‚Äã</a></h2><p>All Notes commands live under <code>icloudbridge notes ...</code>. Ensure you configured <code>notes.enabled=true</code> and <code>notes.remote_folder</code> first.</p><h3 id="notes-sync" tabindex="-1"><code>notes sync</code> <a class="header-anchor" href="#notes-sync" aria-label="Permalink to ‚Äúnotes sync‚Äù">‚Äã</a></h3><p>Bidirectional Notes ‚áÑ Markdown sync. Every option is listed below:</p><table tabindex="0"><thead><tr><th>Option</th><th>Short</th><th>Description</th><th>Default</th></tr></thead><tbody><tr><td><code>--folder NAME</code></td><td><code>-f</code></td><td>Restrict the run to a single Apple Notes folder / Markdown subfolder.</td><td>all folders</td></tr><tr><td><code>--mode MODE</code></td><td><code>-m</code></td><td>Direction: <code>bidirectional</code>, <code>import</code> (Markdown ‚Üí Apple Notes), or <code>export</code> (Apple Notes ‚Üí Markdown).</td><td><code>bidirectional</code></td></tr><tr><td><code>--dry-run</code></td><td><code>-n</code></td><td>Preview changes without writing anything.</td><td></td></tr><tr><td><code>--skip-deletions</code></td><td>‚Äî</td><td>Never delete notes on either side.</td><td></td></tr><tr><td><code>--deletion-threshold N</code></td><td>‚Äî</td><td>Maximum deletions allowed before aborting (<code>-1</code> disables the guard).</td><td><code>5</code></td></tr><tr><td><code>--rich-notes / --no-rich-notes</code></td><td>‚Äî</td><td>When enabled, export read-only Rich Notes HTML snapshots into <code>RichNotes/</code> after sync.</td><td><code>false</code></td></tr><tr><td><code>--shortcut-push / --classic-push</code></td><td>‚Äî</td><td>Force the Shortcut pipeline (default) or fall back to the legacy AppleScript HTML push.</td><td>Shortcut pipeline</td></tr></tbody></table><p>Important behavior:</p><ul><li>The CLI always copies Apple‚Äôs <code>NoteStore.sqlite</code> and runs the rich-notes ripper so you need to grant Full Disk Access to <code>/usr/bin/python3</code>.</li><li>Checklist handling requires the Shortcuts (<code>ICLOUDBRIDGE_NOTES__CHECKLIST_SHORTCUT</code> and <code>ICLOUDBRIDGE_NOTES__CONTENT_SHORTCUT</code>). Use <code>--classic-push</code> only if Shortcuts are broken.</li></ul><p>Examples:</p><div class="language-"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki shiki-themes github-light github-dark" style="--shiki-light:#24292e;--shiki-dark:#e1e4e8;--shiki-light-bg:#fff;--shiki-dark-bg:#24292e;" tabindex="0" dir="ltr"><code><span class="line"><span>icloudbridge notes sync                       # full sync</span></span>
<span class="line"><span>icloudbridge notes sync --dry-run             # preview</span></span>
<span class="line"><span>icloudbridge notes sync --mode export         # Apple ‚Üí Markdown only</span></span>
<span class="line"><span>icloudbridge notes sync --folder &quot;Work&quot; --skip-deletions</span></span>
<span class="line"><span>icloudbridge notes sync --rich-notes --classic-push</span></span></code></pre></div><h3 id="notes-list" tabindex="-1"><code>notes list</code> <a class="header-anchor" href="#notes-list" aria-label="Permalink to ‚Äúnotes list‚Äù">‚Äã</a></h3><p>Shows detected Apple Notes folders (name + UUID). No options.</p><div class="language-"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki shiki-themes github-light github-dark" style="--shiki-light:#24292e;--shiki-dark:#e1e4e8;--shiki-light-bg:#fff;--shiki-dark-bg:#24292e;" tabindex="0" dir="ltr"><code><span class="line"><span>icloudbridge notes list</span></span></code></pre></div><h3 id="notes-status" tabindex="-1"><code>notes status</code> <a class="header-anchor" href="#notes-status" aria-label="Permalink to ‚Äúnotes status‚Äù">‚Äã</a></h3><p>Displays total synced entries, configured Markdown path, and database location.</p><div class="language-"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki shiki-themes github-light github-dark" style="--shiki-light:#24292e;--shiki-dark:#e1e4e8;--shiki-light-bg:#fff;--shiki-dark-bg:#24292e;" tabindex="0" dir="ltr"><code><span class="line"><span>icloudbridge notes status</span></span></code></pre></div><h3 id="notes-reset" tabindex="-1"><code>notes reset</code> <a class="header-anchor" href="#notes-reset" aria-label="Permalink to ‚Äúnotes reset‚Äù">‚Äã</a></h3><p>Clears the Notes sync tracking database. Does <strong>not</strong> delete actual notes.</p><table tabindex="0"><thead><tr><th>Option</th><th>Description</th></tr></thead><tbody><tr><td><code>--yes</code>, <code>-y</code></td><td>Skip the confirmation prompt.</td></tr></tbody></table><div class="language-"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki shiki-themes github-light github-dark" style="--shiki-light:#24292e;--shiki-dark:#e1e4e8;--shiki-light-bg:#fff;--shiki-dark-bg:#24292e;" tabindex="0" dir="ltr"><code><span class="line"><span>icloudbridge notes reset --yes</span></span></code></pre></div><hr><h2 id="reminders-commands" tabindex="-1">Reminders Commands <a class="header-anchor" href="#reminders-commands" aria-label="Permalink to ‚ÄúReminders Commands‚Äù">‚Äã</a></h2><p>Reminders commands require <code>reminders.enabled=true</code>, a CalDAV URL, username, and a password stored via <code>reminders set-password</code>.</p><h3 id="reminders-sync" tabindex="-1"><code>reminders sync</code> <a class="header-anchor" href="#reminders-sync" aria-label="Permalink to ‚Äúreminders sync‚Äù">‚Äã</a></h3><p>Synchronize Apple Reminders with CalDAV calendars.</p><table tabindex="0"><thead><tr><th>Option</th><th>Short</th><th>Description</th><th>Default</th></tr></thead><tbody><tr><td><code>--apple-calendar NAME</code></td><td><code>-a</code></td><td>Manually select an Apple Reminders list when not using auto-discovery.</td><td></td></tr><tr><td><code>--caldav-calendar NAME</code></td><td><code>-c</code></td><td>CalDAV calendar to pair with <code>--apple-calendar</code>.</td><td></td></tr><tr><td><code>--auto / --no-auto</code></td><td>‚Äî</td><td>Force auto-discovery on/off. When omitted we read <code>reminders.sync_mode</code>.</td><td></td></tr><tr><td><code>--dry-run</code></td><td><code>-n</code></td><td>Preview changes without modifying either side.</td><td></td></tr><tr><td><code>--skip-deletions</code></td><td>‚Äî</td><td>Never delete reminders.</td><td></td></tr><tr><td><code>--deletion-threshold N</code></td><td>‚Äî</td><td>Abort if more than <code>N</code> deletions would occur (<code>-1</code> disables).</td><td><code>5</code></td></tr></tbody></table><p>Auto mode syncs every configured mapping (e.g., Apple ‚ÄúReminders‚Äù ‚Üí CalDAV ‚Äútasks‚Äù). Manual mode requires both <code>--apple-calendar</code> and <code>--caldav-calendar</code>.</p><h3 id="reminders-list" tabindex="-1"><code>reminders list</code> <a class="header-anchor" href="#reminders-list" aria-label="Permalink to ‚Äúreminders list‚Äù">‚Äã</a></h3><p>Prints every Apple Reminders list with its UUID. No options.</p><h3 id="reminders-status" tabindex="-1"><code>reminders status</code> <a class="header-anchor" href="#reminders-status" aria-label="Permalink to ‚Äúreminders status‚Äù">‚Äã</a></h3><p>Summarizes current configuration (CalDAV URL/user, whether the password is stored in the keyring, manual calendar overrides, etc.). No options.</p><h3 id="reminders-reset" tabindex="-1"><code>reminders reset</code> <a class="header-anchor" href="#reminders-reset" aria-label="Permalink to ‚Äúreminders reset‚Äù">‚Äã</a></h3><p>Clears the Reminders sync database.</p><table tabindex="0"><thead><tr><th>Option</th><th>Description</th></tr></thead><tbody><tr><td><code>--yes</code>, <code>-y</code></td><td>Skip confirmation.</td></tr></tbody></table><h3 id="reminders-set-password" tabindex="-1"><code>reminders set-password</code> <a class="header-anchor" href="#reminders-set-password" aria-label="Permalink to ‚Äúreminders set-password‚Äù">‚Äã</a></h3><p>Securely store the CalDAV password in the macOS keychain.</p><table tabindex="0"><thead><tr><th>Option</th><th>Description</th></tr></thead><tbody><tr><td><code>--username</code>, <code>-u</code></td><td>Override the username (defaults to the one in config).</td></tr></tbody></table><h3 id="reminders-delete-password" tabindex="-1"><code>reminders delete-password</code> <a class="header-anchor" href="#reminders-delete-password" aria-label="Permalink to ‚Äúreminders delete-password‚Äù">‚Äã</a></h3><p>Remove the stored CalDAV password.</p><table tabindex="0"><thead><tr><th>Option</th><th>Description</th></tr></thead><tbody><tr><td><code>--username</code>, <code>-u</code></td><td>Target username (defaults to config value).</td></tr><tr><td><code>--yes</code>, <code>-y</code></td><td>Skip confirmation.</td></tr></tbody></table><hr><h2 id="password-commands" tabindex="-1">Password Commands <a class="header-anchor" href="#password-commands" aria-label="Permalink to ‚ÄúPassword Commands‚Äù">‚Äã</a></h2><p>All password-related automation is grouped under <code>icloudbridge passwords ...</code>. The database lives alongside other sync metadata and every CSV generated contains plaintext secrets‚Äîdelete those files immediately after importing them.</p><h3 id="passwords-provider-service" tabindex="-1"><code>passwords provider [SERVICE]</code> <a class="header-anchor" href="#passwords-provider-service" aria-label="Permalink to ‚Äúpasswords provider [SERVICE]‚Äù">‚Äã</a></h3><p>Show or set the active downstream service.</p><ul><li><code>SERVICE</code> accepts <code>bitwarden</code>/<code>vaultwarden</code> or <code>nextcloud</code>. When omitted we simply display the current choice.</li></ul><h3 id="passwords-import-apple-csv" tabindex="-1"><code>passwords import-apple CSV</code> <a class="header-anchor" href="#passwords-import-apple-csv" aria-label="Permalink to ‚Äúpasswords import-apple CSV‚Äù">‚Äã</a></h3><p>Seed the database using Apple‚Äôs ‚ÄúExport Passwords‚Ä¶‚Äù CSV (argument is required).</p><h3 id="passwords-export-bitwarden" tabindex="-1"><code>passwords export-bitwarden</code> <a class="header-anchor" href="#passwords-export-bitwarden" aria-label="Permalink to ‚Äúpasswords export-bitwarden‚Äù">‚Äã</a></h3><p>Build a Bitwarden/Vaultwarden-compatible CSV.</p><table tabindex="0"><thead><tr><th>Option</th><th>Description</th></tr></thead><tbody><tr><td><code>--output PATH</code>, <code>-o PATH</code></td><td>Destination file (will be created with <code>0600</code> permissions).</td></tr><tr><td><code>--apple-csv PATH</code></td><td>The original Apple export used to map UUIDs ‚Üî vault entries.</td></tr></tbody></table><h3 id="passwords-import-bitwarden-csv" tabindex="-1"><code>passwords import-bitwarden CSV</code> <a class="header-anchor" href="#passwords-import-bitwarden-csv" aria-label="Permalink to ‚Äúpasswords import-bitwarden CSV‚Äù">‚Äã</a></h3><p>Pull entries from a Bitwarden/Vaultwarden CSV export into the local database.</p><h3 id="passwords-export-apple" tabindex="-1"><code>passwords export-apple</code> <a class="header-anchor" href="#passwords-export-apple" aria-label="Permalink to ‚Äúpasswords export-apple‚Äù">‚Äã</a></h3><p>Generate a CSV that Apple Passwords can import (only entries missing from Apple‚Äôs side).</p><table tabindex="0"><thead><tr><th>Option</th><th>Description</th></tr></thead><tbody><tr><td><code>--output PATH</code>, <code>-o PATH</code></td><td>Destination file.</td></tr><tr><td><code>--bitwarden-csv PATH</code></td><td>Original Bitwarden export to diff against.</td></tr></tbody></table><h3 id="passwords-status" tabindex="-1"><code>passwords status</code> <a class="header-anchor" href="#passwords-status" aria-label="Permalink to ‚Äúpasswords status‚Äù">‚Äã</a></h3><p>Inspect database totals and timestamps for the last Apple/Bitwarden push-pull operations.</p><h3 id="passwords-set-bitwarden-credentials" tabindex="-1"><code>passwords set-bitwarden-credentials</code> <a class="header-anchor" href="#passwords-set-bitwarden-credentials" aria-label="Permalink to ‚Äúpasswords set-bitwarden-credentials‚Äù">‚Äã</a></h3><p>Interactive helper that saves Vaultwarden URL/email/password (and optional OAuth client ID/secret) into the macOS keychain.</p><h3 id="passwords-delete-bitwarden-credentials" tabindex="-1"><code>passwords delete-bitwarden-credentials</code> <a class="header-anchor" href="#passwords-delete-bitwarden-credentials" aria-label="Permalink to ‚Äúpasswords delete-bitwarden-credentials‚Äù">‚Äã</a></h3><p>Remove stored Vaultwarden credentials.</p><table tabindex="0"><thead><tr><th>Option</th><th>Description</th></tr></thead><tbody><tr><td><code>--email ADDRESS</code></td><td>Email to delete (defaults to config value).</td></tr><tr><td><code>--yes</code></td><td>Skip confirmation.</td></tr></tbody></table><h3 id="passwords-set-nextcloud-credentials" tabindex="-1"><code>passwords set-nextcloud-credentials</code> <a class="header-anchor" href="#passwords-set-nextcloud-credentials" aria-label="Permalink to ‚Äúpasswords set-nextcloud-credentials‚Äù">‚Äã</a></h3><p>Store your Nextcloud Passwords username + app password in the keychain. Prompts for URL and user if the config is missing those fields.</p><h3 id="passwords-delete-nextcloud-credentials" tabindex="-1"><code>passwords delete-nextcloud-credentials</code> <a class="header-anchor" href="#passwords-delete-nextcloud-credentials" aria-label="Permalink to ‚Äúpasswords delete-nextcloud-credentials‚Äù">‚Äã</a></h3><p>Delete stored Nextcloud credentials.</p><table tabindex="0"><thead><tr><th>Option</th><th>Description</th></tr></thead><tbody><tr><td><code>--username NAME</code></td><td>Username to delete (defaults to config value).</td></tr><tr><td><code>--yes</code></td><td>Skip confirmation.</td></tr></tbody></table><h3 id="passwords-sync" tabindex="-1"><code>passwords sync</code> <a class="header-anchor" href="#passwords-sync" aria-label="Permalink to ‚Äúpasswords sync‚Äù">‚Äã</a></h3><p>End-to-end synchronization: push Apple passwords into the configured provider and pull changes back into Apple via generated CSVs.</p><table tabindex="0"><thead><tr><th>Option</th><th>Description</th></tr></thead><tbody><tr><td><code>--apple-csv PATH</code></td><td>Required. Fresh Apple ‚ÄúExport Passwords‚Ä¶‚Äù CSV so we can reconcile updates.</td></tr><tr><td><code>--output PATH</code>, <code>-o PATH</code></td><td>Where to store the Apple-import CSV produced during the pull phase.</td></tr><tr><td><code>--bulk</code></td><td>Enable provider-specific bulk APIs when available (faster on Vaultwarden).</td></tr></tbody></table><p>Workflow: authenticate against the chosen provider (credentials must already be in the keychain), compare entries, push additions/updates, optionally download new records from the provider, and tell you how to import them back into Apple.</p><h3 id="passwords-reset" tabindex="-1"><code>passwords reset</code> <a class="header-anchor" href="#passwords-reset" aria-label="Permalink to ‚Äúpasswords reset‚Äù">‚Äã</a></h3><p>Delete everything from the password sync database.</p><table tabindex="0"><thead><tr><th>Option</th><th>Description</th></tr></thead><tbody><tr><td><code>--yes</code></td><td>Skip confirmation.</td></tr></tbody></table><hr><h2 id="service-commands" tabindex="-1">Service Commands <a class="header-anchor" href="#service-commands" aria-label="Permalink to ‚ÄúService Commands‚Äù">‚Äã</a></h2><p><code>icloudbridge service ...</code> manages a macOS LaunchAgent wrapper around <code>icloudbridge serve</code> so you can keep the API server running without the menubar app.</p><h3 id="service-install" tabindex="-1"><code>service install</code> <a class="header-anchor" href="#service-install" aria-label="Permalink to ‚Äúservice install‚Äù">‚Äã</a></h3><table tabindex="0"><thead><tr><th>Option</th><th>Description</th></tr></thead><tbody><tr><td><code>--port N</code></td><td>API port exposed by the service (passed to <code>serve --port</code>).</td></tr><tr><td><code>--start-on-boot / --no-start-on-boot</code></td><td>Auto-start when you log in. Defaults to <code>true</code>.</td></tr></tbody></table><p>Creates <code>~/Library/LaunchAgents/com.icloudbridge.server.plist</code> plus log files in <code>~/Library/Logs/iCloudBridge/</code>.</p><h3 id="service-uninstall" tabindex="-1"><code>service uninstall</code> <a class="header-anchor" href="#service-uninstall" aria-label="Permalink to ‚Äúservice uninstall‚Äù">‚Äã</a></h3><p>Removes the LaunchAgent plist and unloads it from <code>launchctl</code>.</p><h3 id="service-status" tabindex="-1"><code>service status</code> <a class="header-anchor" href="#service-status" aria-label="Permalink to ‚Äúservice status‚Äù">‚Äã</a></h3><p>Reports whether the LaunchAgent is loaded/running and shows the PID if available.</p><h3 id="service-start" tabindex="-1"><code>service start</code> <a class="header-anchor" href="#service-start" aria-label="Permalink to ‚Äúservice start‚Äù">‚Äã</a></h3><p>Loads the plist via <code>launchctl load ...</code>.</p><h3 id="service-stop" tabindex="-1"><code>service stop</code> <a class="header-anchor" href="#service-stop" aria-label="Permalink to ‚Äúservice stop‚Äù">‚Äã</a></h3><p>Unloads the plist via <code>launchctl unload ...</code>.</p><h3 id="service-restart" tabindex="-1"><code>service restart</code> <a class="header-anchor" href="#service-restart" aria-label="Permalink to ‚Äúservice restart‚Äù">‚Äã</a></h3><p>Stops then starts the LaunchAgent (handy after changing the config).</p><hr><h2 id="additional-tips" tabindex="-1">Additional Tips <a class="header-anchor" href="#additional-tips" aria-label="Permalink to ‚ÄúAdditional Tips‚Äù">‚Äã</a></h2><ul><li><code>icloudbridge db-paths</code> and <code>icloudbridge notes status</code> are safe read-only commands you can use before any sync to ensure the databases exist where you expect them.</li><li>The CLI respects environment variables defined in <code>docs/config.md</code> (e.g., <code>ICLOUDBRIDGE_NOTES__REMOTE_FOLDER</code>). CLI flags override environment variables, which override the config file.</li><li>All password-related commands touch plaintext CSV files‚Äîtreat them with the same care as any exported secrets. Delete them after importing.</li><li>Commands that interact with Apple Notes/Reminders will launch permission prompts the first time they run. Accept them in System Settings ‚Üí Privacy &amp; Security.</li></ul><p>You now have a single reference for every available CLI option. Happy syncing!üå•Ô∏è</p>`,129)])])}const u=t(o,[["render",r]]);export{b as __pageData,u as default};
